import { DefaultConfigType } from "./DefaultConfig";

export const Config = (ConfigObject: DefaultConfigType) => {
  const { component, api, router, form } = ConfigObject;
  return [
    "//React igniter config file",
    "//This file is generated by the react igniter generator",
    "",
    "module.exports = {",
    `// Component generation config`,
    `  version: "${ConfigObject.version}",`,
    `  component: {`,
    `    type: "${component.type}", // The type of generated files, string value, ["typescript", "javascript"]`,
    `    test: ${component.test}, // Should a test file be generated, boolean value, [true, false]`,
    `    style: "${component.style}", // Styling module to be generated with the component, string value, ["css", "scss", "styled-components", "none"]`,
    `    storybook: ${component.storybook}, // Should a storybook file be generated, boolean value, [true, false]`,
    `    exportType: "${component.exportType}", // Export type, string value, ["named", "default"]`,
    `    includeIndex: ${component.includeIndex}, // Should an index file be generated, boolean value, [true, false]`,
    `    translations: ${component.translations}, // Should translations be generated, boolean value, [true, false]`,
    `    rootDir: "${component.rootDir}", // The root directory of the project, string value, defaults to "src"`,
    `    disabled: ${component.disabled}, // Should the component generation be disabled, boolean value, [true, false]`,
    `  },`,
    `// API generation config`,
    `  api: {`,
    `    type: "${api.type}", // The type of generated files, string value, ["typescript", "javascript"]`,
    `    init: ${api.init}, // should the BaseApi be initialized, boolean value, [true, false]`,
    `    crud: ${api.crud}, // Should the api generated contain CRUD, boolean value [true, false]`,
    `    prefix: "${api.prefix}", // Prefix for BaseApi initializer, string value, [defaults to /]`,
    `    rootDir: "${api.rootDir}", // The root directory of the project, string value, defaults to "src"`,
    `    disabled: ${api.disabled}, // Should the api generation be disabled, boolean value, [true, false]`,
    `  },`,
    `// Router generation config`,
    `  router: {`,
    `    type: "${router.type}", // The type of generated files, string value, ["typescript", "javascript"]`,
    `    main: ${router.main}, // Should the generated output be a main application router [true, false]`,
    `    withTabs: ${router.withTabs}, // Should the generated router contain tabs [true, false]`,
    `    rootDir: "${router.rootDir}", // The root directory of the project, string value, defaults to "src"`,
    `    disabled: ${router.disabled}, // Should the router generation be disabled, boolean value, [true, false]`,
    `  },`,
    `// Form generation config`,
    `  form: {`,
    `    withContext: ${form.withContext}, // Should the generated form contain context wrapper [true, false]`,
    `    withValidation: ${form.withValidation}, // Should the generated form contain validation [true, false]`,
    `    type: "${form.type}", // The type of generated files, string value, ["typescript", "javascript"]`,
    `    validationMode: "${form.validationMode}", // Validation mode, string value, ["onSubmit", "onChange", "onBlur", "all", "onTouched"]`,
    `    revalidationMode: "${form.revalidationMode}", // Revalidation mode, string value, ["onChange", "onBlur", "onSubmit"]`,
    `    exportType: "${form.exportType}", // Export type, string value, ["named", "default"]`,
    `    includeIndex: ${form.includeIndex}, // Should an index file be generated, boolean value, [true, false]`,
    `    rootDir: "${form.rootDir}", // The root directory of the project, string value, defaults to "src"`,
    `    disabled: ${form.disabled}, // Should the form generation be disabled, boolean value, [true, false]`,
    `  },`,
    "}",
    "",
  ].join("\n");
};
